import{_ as a,o,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-2312 Procedure on appeal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2312.md","filePath":"vacode/15.2-2312.md"}'),i={name:"vacode/15.2-2312.md"},n=e("h1",{id:"§-15-2-2312-procedure-on-appeal",tabindex:"-1"},[t("§ 15.2-2312 Procedure on appeal. "),e("a",{class:"header-anchor",href:"#§-15-2-2312-procedure-on-appeal","aria-label":'Permalink to "§ 15.2-2312 Procedure on appeal."'},"​")],-1),c=e("p",null,"The board shall fix a reasonable time for the hearing of an application or appeal, give public notice thereof as well as due notice to the parties in interest and make its decision within ninety days of the filing of the application or appeal. In exercising its powers the board may reverse or affirm, wholly or partly, or may modify, an order, requirement, decision or determination appealed from. The concurring vote of a majority of the membership of the board shall be necessary to reverse any order, requirement, decision or determination of an administrative officer or to decide in favor of the applicant on any matter upon which it is required to pass under the ordinance or to effect any variance from the ordinance. The board shall keep minutes of its proceedings and other official actions which shall be filed in the office of the board and shall be public records. The chairman of the board, or in his absence the acting chairman, may administer oaths and compel the attendance of witnesses.",-1),s=e("p",null,"1975, c. 521, § 15.1-496.2; 1983, c. 444; 1986, c. 483; 1997, c. 587.",-1),d=[n,c,s];function h(l,p,f,m,u,_){return o(),r("div",null,d)}const v=a(i,[["render",h]]);export{y as __pageData,v as default};
