import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-2316.7 Negotiations; siting agreement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2316.7.md","filePath":"vacode/15.2-2316.7.md"}'),n={name:"vacode/15.2-2316.7.md"},o=i('<h1 id="§-15-2-2316-7-negotiations-siting-agreement" tabindex="-1">§ 15.2-2316.7 Negotiations; siting agreement. <a class="header-anchor" href="#§-15-2-2316-7-negotiations-siting-agreement" aria-label="Permalink to &quot;§ 15.2-2316.7 Negotiations; siting agreement.&quot;">​</a></h1><p>A. Any applicant for a solar project or an energy storage project shall give to the host locality written notice of the applicant&#39;s intent to locate in such locality and request a meeting. Such applicant shall meet, discuss, and negotiate a siting agreement with such locality.</p><p>B. The siting agreement may include terms and conditions, including (i) mitigation of any impacts of such solar project or energy storage project; (ii) financial compensation to the host locality to address capital needs set out in the (a) capital improvement plan adopted by the host locality, (b) current fiscal budget of the host locality, or (c) fiscal fund balance policy adopted by the host locality; or (iii) assistance by the applicant in the deployment of broadband, as defined in § <a href="/vacode/56-585.1:9/">56-585.1:9</a>, in such locality.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0802" target="_blank" rel="noreferrer">802</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0058" target="_blank" rel="noreferrer">58</a>.</p>',4),r=[o];function s(c,l,p,g,d,h){return t(),a("div",null,r)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
