import{_ as o,o as t,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-2405 How imposed.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2405.md","filePath":"vacode/15.2-2405.md"}'),r={name:"vacode/15.2-2405.md"},s=e("h1",{id:"§-15-2-2405-how-imposed",tabindex:"-1"},[n("§ 15.2-2405 How imposed. "),e("a",{class:"header-anchor",href:"#§-15-2-2405-how-imposed","aria-label":'Permalink to "§ 15.2-2405 How imposed."'},"​")],-1),i=e("p",null,"Such improvements may be ordered by the governing body and the cost thereof apportioned in pursuance of an agreement between the governing body and the abutting landowners, and, in the absence of such an agreement, the cost of improvements which is to be defrayed in whole or in part by such local tax or assessment, may in cities and towns be ordered on a petition from not less than three-fourths of the landowners to be affected thereby, or in counties on a petition from not less than sixty percent of the landowners to be affected thereby or by a two-thirds vote of all the members elected to the governing body. Notice shall be given to the abutting landowners, notifying them when and where they may appear before the governing body, or some committee thereof, or the administrative board or other similar board of the locality to whom the matter may be referred, to be heard in favor of or against such improvements.",-1),d=e("p",null,"Code 1950, § 15-670; 1962, c. 623, § 15.1-240; 1997, c. 587.",-1),h=[s,i,d];function c(m,l,f,p,b,_){return t(),a("div",null,h)}const w=o(r,[["render",c]]);export{u as __pageData,w as default};
