import{_ as o,o as a,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-2412 Docketing of abstracts of resolutions or ordinances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2412.md","filePath":"vacode/15.2-2412.md"}'),r={name:"vacode/15.2-2412.md"},s=e("h1",{id:"§-15-2-2412-docketing-of-abstracts-of-resolutions-or-ordinances",tabindex:"-1"},[t("§ 15.2-2412 Docketing of abstracts of resolutions or ordinances. "),e("a",{class:"header-anchor",href:"#§-15-2-2412-docketing-of-abstracts-of-resolutions-or-ordinances","aria-label":'Permalink to "§ 15.2-2412 Docketing of abstracts of resolutions or ordinances."'},"​")],-1),i=e("p",null,[t("When any improvement is authorized for which assessments may be made against the abutting landowners, the governing body may, before the amount to be finally assessed against or apportioned to each landowner or fixed by agreement is determined, cause to be recorded in the deed book of the circuit court clerk's office for such locality, an abstract of the resolution or ordinance authorizing such improvement showing the ownership and location of the property to be affected by the proposed improvement and the estimated amount that will be assessed against or apportioned to each landowner or fixed by agreement with him and the same shall be indexed in the name of the owner of the property. Such assessment shall be a lien solely on the abutting land as provided in § "),e("a",{href:"http://law.lis.virginia.gov/vacode/15.2-2411/",target:"_blank",rel:"noreferrer"},"15.2-2411"),t(".")],-1),d=e("p",null,"After the completion of the improvement, the estimated amount shall be amended to show the amount finally assessed against or apportioned to each landowner or fixed by agreement with him, which final amount shall in no event exceed the estimated amount for the improvements as initially authorized. The amount finally assessed against or apportioned to each landowner may be greater than the initially assessed amount when the increased amount is for additional work being performed when the work was requested by the landowner and the additional work and its estimated amount is written into a separate agreement between the locality and the affected landowner. From the time of the docketing of such abstract, any purchaser of, or creditor acquiring a lien on, any of the property described therein shall be deemed to have had notice of the proposed assessment.",-1),h=e("p",null,[t("Code 1950, § 15-677; 1962, c. 623, § 15.1-247; 1964, c. 521; 1985, c. 169; 1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0222",target:"_blank",rel:"noreferrer"},"222"),t("; 1997, c. 587.")],-1),c=[s,i,d,h];function l(m,f,p,u,b,g){return a(),n("div",null,c)}const y=o(r,[["render",l]]);export{_ as __pageData,y as default};
