import{_ as i,o as t,c as e,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-2413.6 Local ordinances related to tourism improvement districts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2413.6.md","filePath":"vacode/15.2-2413.6.md"}'),r={name:"vacode/15.2-2413.6.md"},s=o('<h1 id="§-15-2-2413-6-local-ordinances-related-to-tourism-improvement-districts" tabindex="-1">§ 15.2-2413.6 Local ordinances related to tourism improvement districts. <a class="header-anchor" href="#§-15-2-2413-6-local-ordinances-related-to-tourism-improvement-districts" aria-label="Permalink to &quot;§ 15.2-2413.6 Local ordinances related to tourism improvement districts.&quot;">​</a></h1><p>A. Any locality establishing a tourism improvement district may enact ordinances on any of the following subjects that provide for:</p><ol><li><p>Activities and other additional services required for tourism promotion or events or for enhancement of the tourism improvement district;</p></li><li><p>Activities in the tourism improvement district that will fund the promotion of tourism activities in the tourism improvement district, including acquiring, constructing, installing, or maintaining capital improvements;</p></li><li><p>Operating and maintaining any tourism improvement district activity;</p></li><li><p>The charging of fees on all benefited businesses within a tourism improvement district, which shall be charged on the basis of the estimated benefit to such businesses within the tourism improvement district;</p></li><li><p>The classifying of businesses for purposes of determining the benefit to the businesses of the activities provided pursuant to this article;</p></li><li><p>A process for the collection of revenues from fees from benefited businesses; and</p></li><li><p>Forming a tourism improvement district in cooperation with, and that includes, other localities.</p></li></ol><p>B. After establishing a tourism improvement district, a locality shall not decrease the level of publicly funded tourism promotion services in a tourism improvement district existing prior to the creation of such tourism improvement district.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>.</p>',5),n=[s];function a(c,l,m,d,p,h){return t(),e("div",null,n)}const v=i(r,[["render",a]]);export{f as __pageData,v as default};
