import{_ as e,o,c as n,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-2705 Required provisions in contract; election of governing authority; financial plan; management plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2705.md","filePath":"vacode/15.2-2705.md"}'),i={name:"vacode/15.2-2705.md"},t=a('<h1 id="§-15-2-2705-required-provisions-in-contract-election-of-governing-authority-financial-plan-management-plan" tabindex="-1">§ 15.2-2705 Required provisions in contract; election of governing authority; financial plan; management plan. <a class="header-anchor" href="#§-15-2-2705-required-provisions-in-contract-election-of-governing-authority-financial-plan-management-plan" aria-label="Permalink to &quot;§ 15.2-2705 Required provisions in contract; election of governing authority; financial plan; management plan.&quot;">​</a></h1><p>Any intergovernmental contract entered into pursuant to this chapter for the purpose of establishing a group self-insurance pool shall provide:</p><ol><li><p>For election by pool members of a governing authority for the pool, which may be a board of directors, a majority of whom shall be elected or appointed officials of pool members.</p></li><li><p>A financial plan setting forth in general terms:</p></li></ol><p>a. The insurance coverages to be offered by the group self-insurance pool, applicable deductible levels, and the maximum level of claims which the pool will self-insure;</p><p>b. The amount of cash reserves to be set aside for the payment of claims;</p><p>c. The amount of insurance to be purchased by the pool to provide coverage over and above the claims which are not to be satisfied directly from the pool&#39;s resources; and</p><p>d. The amount, if any, of aggregate excess insurance coverage to be purchased and maintained in the event that the group self-insurance pool&#39;s resources are exhausted in a given fiscal period.</p><ol start="3"><li>A plan of management which provides for all of the following:</li></ol><p>a. The means of establishing the governing authority of the pool;</p><p>b. The responsibility of the governing authority for fixing contributions to the pool, maintaining reserves, levying and collecting assessments for deficiencies, disposing of surpluses, and administration of the pool in the event of termination or insolvency;</p><p>c. The basis upon which new members may be admitted to, and existing members may leave, the pool;</p><p>d. The identification of funds and reserves by exposure areas; and</p><p>e. Such other provisions as are necessary or desirable for the operation of the pool.</p><p>1986, cc. 520, 556, § 15.1-503.4:5; 1997, c. 587.</p>',14),r=[t];function s(l,c,p,h,f,d){return o(),n("div",null,r)}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
