import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-2824 Prohibitions on smoking generally; penalty for violation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2824.md","filePath":"vacode/15.2-2824.md"}'),n={name:"vacode/15.2-2824.md"},r=o('<h1 id="§-15-2-2824-prohibitions-on-smoking-generally-penalty-for-violation" tabindex="-1">§ 15.2-2824 Prohibitions on smoking generally; penalty for violation. <a class="header-anchor" href="#§-15-2-2824-prohibitions-on-smoking-generally-penalty-for-violation" aria-label="Permalink to &quot;§ 15.2-2824 Prohibitions on smoking generally; penalty for violation.&quot;">​</a></h1><p>A. Smoking shall be prohibited in (i) elevators, regardless of capacity, except in any open material hoist elevator not intended for use by the general public; (ii) public school buses; (iii) the interior of any public elementary, intermediate, and secondary school; (iv) hospital emergency rooms; (v) local or district health departments; (vi) polling rooms; (vii) indoor service lines and cashier lines; (viii) public restrooms in any building owned or leased by the Commonwealth or any agency thereof; (ix) the interior of a child day center licensed pursuant to § <a href="/vacode/22.1-289.01/">22.1-289.011</a> that is not also used for residential purposes; however, this prohibition shall not apply to any area of a building not utilized by a child day center, unless otherwise prohibited by this chapter; and (x) public restrooms of health care facilities.</p><p>B. No person shall smoke in any area or place specified in subsection A and any person who continues to smoke in such area or place after having been asked to refrain from smoking shall be subject to a civil penalty of not more than $25.</p><p>C. Civil penalties assessed under this section shall be paid into the Virginia Health Care Fund established under § <a href="/vacode/32.1-366/">32.1-366</a>.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',5),t=[r];function l(s,c,h,p,d,f){return i(),a("div",null,t)}const u=e(n,[["render",l]]);export{b as __pageData,u as default};
