import{_ as e,o,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-2826 Designation of \\"No-Smoking\\" areas; smoking prohibited in \\"No-Smoking\\" areas; penalty for violation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2826.md","filePath":"vacode/15.2-2826.md"}'),t={name:"vacode/15.2-2826.md"},n=a('<h1 id="§-15-2-2826-designation-of-no-smoking-areas-smoking-prohibited-in-no-smoking-areas-penalty-for-violation" tabindex="-1">§ 15.2-2826 Designation of &quot;No-Smoking&quot; areas; smoking prohibited in &quot;No-Smoking&quot; areas; penalty for violation. <a class="header-anchor" href="#§-15-2-2826-designation-of-no-smoking-areas-smoking-prohibited-in-no-smoking-areas-penalty-for-violation" aria-label="Permalink to &quot;§ 15.2-2826 Designation of &quot;No-Smoking&quot; areas; smoking prohibited in &quot;No-Smoking&quot; areas; penalty for violation.&quot;">​</a></h1><p>A. The proprietor or other person in charge of (i) an educational facility, except any public elementary, intermediate, or secondary school; (ii) a health care facility; (iii) a retail establishment of 15,000 square feet or more serving the general public, including, but not limited to, department stores, grocery stores, drug stores, clothing stores, and shoe stores; and (iv) recreational facilities shall designate reasonable no-smoking areas, considering the nature of the use and the size of the building.</p><p>B. The proprietor or other person in charge of a space subject to the provisions of this section shall post signs conspicuous to public view stating &quot;Smoking Permitted&quot; or &quot;No Smoking.&quot; Any person failing to post such signs shall be subject to a civil penalty of not more than $25.</p><p>C. No person shall smoke in a designated no-smoking area and any person who continues to smoke in such area after having been asked to refrain from smoking shall be subject to a civil penalty of not more than $25.</p><p>D. Civil penalties assessed under this section shall be paid into the Virginia Health Care Fund established under § <a href="http://law.lis.virginia.gov/vacode/32.1-366/" target="_blank" rel="noreferrer">32.1-366</a>.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>.</p>',6),r=[n];function s(l,c,p,g,h,d){return o(),i("div",null,r)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
