import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-2827 Responsibility of building proprietors and managers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2827.md","filePath":"vacode/15.2-2827.md"}'),o={name:"vacode/15.2-2827.md"},t=r('<h1 id="§-15-2-2827-responsibility-of-building-proprietors-and-managers" tabindex="-1">§ 15.2-2827 Responsibility of building proprietors and managers. <a class="header-anchor" href="#§-15-2-2827-responsibility-of-building-proprietors-and-managers" aria-label="Permalink to &quot;§ 15.2-2827 Responsibility of building proprietors and managers.&quot;">​</a></h1><p>Except as provided in § <a href="http://law.lis.virginia.gov/vacode/15.2-2825/" target="_blank" rel="noreferrer">15.2-2825</a>, proprietors or persons who manage or otherwise control any building, structure, space, place, or area governed by this chapter in which smoking is not otherwise prohibited may designate rooms or areas in which smoking is permitted as follows:</p><ol><li><p>Designated smoking areas shall not encompass so much of the building, structure, space, place, or area open to the general public that reasonable no-smoking areas, considering the nature of the use and the size of the building, are not provided;</p></li><li><p>Designated smoking areas shall be separate to the extent reasonably practicable from those rooms or areas entered by the general public in the normal use of the particular business or institution; and</p></li><li><p>In designated smoking areas, ventilation systems and existing physical barriers shall be used when reasonably practicable to minimize the permeation of smoke into no-smoking areas. However, this chapter shall not be construed as requiring physical modifications or alterations to any structure.</p></li></ol><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>.</p>',4),s=[t];function n(l,p,c,d,h,g){return a(),i("div",null,s)}const u=e(o,[["render",n]]);export{b as __pageData,u as default};
