import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-2831 Other ordinances not authorized.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2831.md","filePath":"vacode/15.2-2831.md"}'),t={name:"vacode/15.2-2831.md"},n=o('<h1 id="§-15-2-2831-other-ordinances-not-authorized" tabindex="-1">§ 15.2-2831 Other ordinances not authorized. <a class="header-anchor" href="#§-15-2-2831-other-ordinances-not-authorized" aria-label="Permalink to &quot;§ 15.2-2831 Other ordinances not authorized.&quot;">​</a></h1><p>The provisions of §§ <a href="http://law.lis.virginia.gov/vacode/15.2-2828/" target="_blank" rel="noreferrer">15.2-2828</a>, <a href="http://law.lis.virginia.gov/vacode/15.2-2829/" target="_blank" rel="noreferrer">15.2-2829</a>, and <a href="http://law.lis.virginia.gov/vacode/15.2-2830/" target="_blank" rel="noreferrer">15.2-2830</a> shall not be construed to allow local regulation of smoking in:</p><ol><li><p>Conference or meeting rooms and public or private assembly rooms while such rooms are being used for private functions;</p></li><li><p>Private work places;</p></li><li><p>Areas of enclosed shopping centers or malls that are external to the retail stores therein, are used by customers as a route of travel from one store to another, and consist primarily of walkways and seating arrangements; or</p></li><li><p>Lobby areas of hotels, motels, and other establishments open to the general public for overnight accommodation.</p></li></ol><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>.</p>',4),i=[n];function s(l,c,h,d,p,_){return r(),a("div",null,i)}const m=e(t,[["render",s]]);export{g as __pageData,m as default};
