import{_ as e,o as n,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-2833 Enforcement of ordinances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-2833.md","filePath":"vacode/15.2-2833.md"}'),r={name:"vacode/15.2-2833.md"},t=o('<h1 id="§-15-2-2833-enforcement-of-ordinances" tabindex="-1">§ 15.2-2833 Enforcement of ordinances. <a class="header-anchor" href="#§-15-2-2833-enforcement-of-ordinances" aria-label="Permalink to &quot;§ 15.2-2833 Enforcement of ordinances.&quot;">​</a></h1><p>A. Any ordinance may provide a civil penalty of not more than $25 for violations of any provision of such ordinance.</p><p>B. Any ordinance may provide that no person shall smoke in a designated no-smoking area and any person who continues to smoke in such area after being asked to refrain from smoking may be subject to a civil penalty of not more than $25.</p><p>C. Any ordinance shall provide that any law-enforcement officer may issue a summons regarding a violation of the ordinance.</p><p>D. Any civil penalties assessed under this section shall be paid into the treasury of the locality where the offense occurred and shall be expended solely for public health purposes.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0153" target="_blank" rel="noreferrer">153</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>.</p>',6),i=[t];function s(c,l,d,f,p,h){return n(),a("div",null,i)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
