import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-3222 What order to be entered by the Supreme Court or the Court of Appeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-3222.md","filePath":"vacode/15.2-3222.md"}'),a={name:"vacode/15.2-3222.md"},h=o('<h1 id="§-15-2-3222-what-order-to-be-entered-by-the-supreme-court-or-the-court-of-appeals" tabindex="-1">§ 15.2-3222 What order to be entered by the Supreme Court or the Court of Appeals. <a class="header-anchor" href="#§-15-2-3222-what-order-to-be-entered-by-the-supreme-court-or-the-court-of-appeals" aria-label="Permalink to &quot;§ 15.2-3222 What order to be entered by the Supreme Court or the Court of Appeals.&quot;">​</a></h1><p>If the judgment of the special court is reversed on appeal, or if the judgment is modified, the Court of Appeals shall enter such order as the special court should have entered, certify a copy of the order to the Secretary of the Commonwealth, and such order shall be final unless appealed to the Supreme Court. If an appeal is taken from the judgment of the Court of Appeals, the Supreme Court, in matters in which it grants the petition for appeal, shall consider the appeal consistent with the procedures set forth in § <a href="/vacode/15.2-3221/">15.2-3221</a>, shall enter such order as the special court should have entered, and shall certify the order to the Secretary of the Commonwealth.</p><p>Code 1950, § 15-152.20; 1952, c. 328; 1962, c. 623, § 15.1-1050; 1970, c. 751; 1978, c. 642; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',3),s=[h];function n(p,c,l,d,i,u){return t(),r("div",null,s)}const m=e(a,[["render",n]]);export{_ as __pageData,m as default};
