import{_ as t,o,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-3307 Election of city barred from annexation to be treated as immune county.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-3307.md","filePath":"vacode/15.2-3307.md"}'),r={name:"vacode/15.2-3307.md"},i=e("h1",{id:"§-15-2-3307-election-of-city-barred-from-annexation-to-be-treated-as-immune-county",tabindex:"-1"},[n("§ 15.2-3307 Election of city barred from annexation to be treated as immune county. "),e("a",{class:"header-anchor",href:"#§-15-2-3307-election-of-city-barred-from-annexation-to-be-treated-as-immune-county","aria-label":'Permalink to "§ 15.2-3307 Election of city barred from annexation to be treated as immune county."'},"​")],-1),s=e("p",null,"Notwithstanding any other provision of law, any city that is barred or that may hereafter become barred from further annexation may, by resolution passed by a majority vote of its governing body, elect to be treated the same as an immune county for purposes of state police services and for the maintenance and construction of streets and highways. Such election shall be exercised by notifying the Governor of the election at least two years prior to the beginning of the biennium in which it takes effect. If, after a minimum period of eight years following the date upon which such treatment has become effective, a city wishes to terminate such treatment as an immune county, it shall notify the Governor of its intention to return to being treated as a city for such purposes. Such return shall become effective two years after such notification to the Governor.",-1),c=e("p",null,"1979, c. 85, § 15.1-977.24; 1997, c. 587.",-1),f=[i,s,c];function d(h,m,l,u,y,b){return o(),a("div",null,f)}const v=t(r,[["render",d]]);export{_ as __pageData,v as default};
