import{_ as t,o as e,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-3800 Ordinance petitioning court for city status; appointment of special court.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-3800.md","filePath":"vacode/15.2-3800.md"}'),a={name:"vacode/15.2-3800.md"},r=i('<h1 id="§-15-2-3800-ordinance-petitioning-court-for-city-status-appointment-of-special-court" tabindex="-1">§ 15.2-3800 Ordinance petitioning court for city status; appointment of special court. <a class="header-anchor" href="#§-15-2-3800-ordinance-petitioning-court-for-city-status-appointment-of-special-court" aria-label="Permalink to &quot;§ 15.2-3800 Ordinance petitioning court for city status; appointment of special court.&quot;">​</a></h1><p>Any town, except a town located within a county or any portion of a county granted immunity as provided by Chapter 33 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-3300/" target="_blank" rel="noreferrer">15.2-3300</a> et seq.) from the incorporation of new cities within its boundaries, may, by ordinance passed by a recorded majority vote of all the members thereof, petition the circuit court for the county within which the town lies, alleging that the town meets the criteria set out in subsection A of § <a href="http://law.lis.virginia.gov/vacode/15.2-3807/" target="_blank" rel="noreferrer">15.2-3807</a>, for an order granting city status to the town. The circuit court with which the petition is filed shall notify the Supreme Court, which shall appoint a special court to hear the case as prescribed by Chapter 30 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-3000/" target="_blank" rel="noreferrer">15.2-3000</a> et seq.) of this title.</p><p>1979, c. 85, § 15.1-982.1; 1997, c. 587.</p>',3),n=[r];function c(s,p,h,l,d,u){return e(),o("div",null,n)}const m=t(a,[["render",c]]);export{_ as __pageData,m as default};
