import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-3831 Registrars and their duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-3831.md","filePath":"vacode/15.2-3831.md"}'),i={name:"vacode/15.2-3831.md"},n=a('<h1 id="§-15-2-3831-registrars-and-their-duties" tabindex="-1">§ 15.2-3831 Registrars and their duties. <a class="header-anchor" href="#§-15-2-3831-registrars-and-their-duties" aria-label="Permalink to &quot;§ 15.2-3831 Registrars and their duties.&quot;">​</a></h1><p>Upon its appointment, the electoral board for a city created under this chapter shall appoint a general registrar pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-110/" target="_blank" rel="noreferrer">24.2-110</a>. The registration records of voters residing in the city shall be transferred, and the appropriate notice given, in accordance with § <a href="http://law.lis.virginia.gov/vacode/24.2-114/" target="_blank" rel="noreferrer">24.2-114</a>. At any time the books are not closed pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-416/" target="_blank" rel="noreferrer">24.2-416</a>, any person residing in the city who has not registered shall be entitled to register and vote in the city if he would have been entitled to register and vote in the county.</p><p>Code 1950, § 15-105; 1962, c. 623, § 15.1-1006; 1997, c. 587; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1015" target="_blank" rel="noreferrer">1015</a>.</p>',3),o=[n];function s(d,c,l,h,_,p){return t(),r("div",null,o)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
