import{_ as a,o as e,c as t,z as o,a as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-417 Examination and audit of accounts and books.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-417.md","filePath":"vacode/15.2-417.md"}'),i={name:"vacode/15.2-417.md"},s=o("h1",{id:"§-15-2-417-examination-and-audit-of-accounts-and-books",tabindex:"-1"},[n("§ 15.2-417 Examination and audit of accounts and books. "),o("a",{class:"header-anchor",href:"#§-15-2-417-examination-and-audit-of-accounts-and-books","aria-label":'Permalink to "§ 15.2-417 Examination and audit of accounts and books."'},"​")],-1),c=o("p",null,"The board shall require an annual audit of the books of every county officer who handles public funds to be made by an accountant who is not a regular officer or employee of the county and who is thoroughly qualified by training and experience. An audit by the Auditor of Public Accounts, under the provisions of law, may be considered as having satisfied the requirements of this section. The board may at any time order an examination or audit of the accounts of any officer or employee of the county government. Upon the death, resignation, removal or expiration of the term of any county officer, the board shall cause an audit and investigation of the accounts of such officer to be made. If, as a result of any such audit, an officer is found indebted to the county, the board shall proceed forthwith to collect such indebtedness.",-1),d=o("p",null,"Code 1950, § 15-380; 1962, c. 623, § 15.1-716; 1997, c. 587.",-1),r=[s,c,d];function u(f,h,l,m,b,p){return e(),t("div",null,r)}const x=a(i,[["render",u]]);export{y as __pageData,x as default};
