import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 15.2-4208 General duties of planning district commissions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-4208.md","filePath":"vacode/15.2-4208.md"}'),n={name:"vacode/15.2-4208.md"},o=t('<h1 id="§-15-2-4208-general-duties-of-planning-district-commissions" tabindex="-1">§ 15.2-4208 General duties of planning district commissions. <a class="header-anchor" href="#§-15-2-4208-general-duties-of-planning-district-commissions" aria-label="Permalink to &quot;§ 15.2-4208 General duties of planning district commissions.&quot;">​</a></h1><p>Planning district commissions shall have the following duties and authority:</p><ol><li><p>To conduct studies on issues and problems of regional significance;</p></li><li><p>To identify and study potential opportunities for state and local cost savings and staffing efficiencies through coordinated governmental efforts;</p></li><li><p>To identify mechanisms for the coordination of state and local interests on a regional basis;</p></li><li><p>To implement services upon request of member localities;</p></li><li><p>To provide technical assistance to state government and member localities;</p></li><li><p>To serve as a liaison between localities and state agencies as requested;</p></li><li><p>To review local government aid applications as required by § <a href="http://law.lis.virginia.gov/vacode/15.2-4213/" target="_blank" rel="noreferrer">15.2-4213</a> and other state or federal law or regulation;</p></li><li><p>To conduct strategic planning for the region as required by §§ <a href="http://law.lis.virginia.gov/vacode/15.2-4209/" target="_blank" rel="noreferrer">15.2-4209</a> through <a href="http://law.lis.virginia.gov/vacode/15.2-4212/" target="_blank" rel="noreferrer">15.2-4212</a>;</p></li><li><p>To develop regional functional area plans as deemed necessary by the commission or as requested by member localities;</p></li><li><p>To assist state agencies, as requested, in the development of substate plans;</p></li><li><p>To participate in a statewide geographic information system, the Virginia Geographic Information Network, as directed by the Department of Planning and Budget; and</p></li><li><p>To collect and maintain demographic, economic and other data concerning the region and member localities, and act as a state data center affiliate in cooperation with the Virginia Employment Commission.</p></li></ol><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0732" target="_blank" rel="noreferrer">732</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>, § 15.1-1405.1; 1997, c. 587; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0686" target="_blank" rel="noreferrer">686</a>.</p>',4),r=[o];function s(l,c,d,p,g,f){return i(),a("div",null,r)}const u=e(n,[["render",s]]);export{h as __pageData,u as default};
