import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-4215 Annual report required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-4215.md","filePath":"vacode/15.2-4215.md"}'),n={name:"vacode/15.2-4215.md"},i=r('<h1 id="§-15-2-4215-annual-report-required" tabindex="-1">§ 15.2-4215 Annual report required. <a class="header-anchor" href="#§-15-2-4215-annual-report-required" aria-label="Permalink to &quot;§ 15.2-4215 Annual report required.&quot;">​</a></h1><p>Each planning district commission shall submit an annual report by September 1 to its member local governments and the Department of Housing and Community Development in accordance with a format prescribed by the Department. The annual report shall contain at a minimum a description of the activities conducted by the planning district commission during the preceding fiscal year, including how the commission met the provisions of this chapter, and information showing the sources and amounts of funding provided to the commission. The Department of Housing and Community Development shall summarize the annual reports in a report to be distributed in accordance with § <a href="http://law.lis.virginia.gov/vacode/36-139.6/" target="_blank" rel="noreferrer">36-139.6</a>.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0732" target="_blank" rel="noreferrer">732</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>, § 15.1-1411.1; 1997, c. 587.</p>',3),o=[i];function s(c,l,d,p,m,h){return t(),a("div",null,o)}const f=e(n,[["render",s]]);export{_ as __pageData,f as default};
