import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-4406 Provisions of district ordinances for districts of local significance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-4406.md","filePath":"vacode/15.2-4406.md"}'),o={name:"vacode/15.2-4406.md"},r=a('<h1 id="§-15-2-4406-provisions-of-district-ordinances-for-districts-of-local-significance" tabindex="-1">§ 15.2-4406 Provisions of district ordinances for districts of local significance. <a class="header-anchor" href="#§-15-2-4406-provisions-of-district-ordinances-for-districts-of-local-significance" aria-label="Permalink to &quot;§ 15.2-4406 Provisions of district ordinances for districts of local significance.&quot;">​</a></h1><p>Any district ordinance adopted by the local governing body in order to create or renew an agricultural, forestal, or agricultural and forestal district shall include the following provisions:</p><ol><li><p>That no parcel included within the district shall be developed to a more intensive use than its existing use at the time of adoption of the ordinance creating the district for eight years from the date of adoption of such ordinance;</p></li><li><p>That no parcel added to an already created district shall be developed to a more intensive use than its existing use at the time of addition to the district for eight years from the date of adoption of the original district ordinance;</p></li><li><p>That land used in agricultural and forestal production within the agricultural and forestal district of local significance shall automatically qualify for an agricultural or forestal value assessment on such land pursuant to Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-3229/" target="_blank" rel="noreferrer">58.1-3229</a> et seq.) of Chapter 32 of Title 58.1, if the requirements for such assessment contained therein are satisfied, whether or not a local land-use plan or local ordinance pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-3231/" target="_blank" rel="noreferrer">58.1-3231</a> has been adopted;</p></li><li><p>That the district shall be reviewed by the local governing body at the end of the eight-year period and that it may by ordinance renew the district or modification thereof for another eight-year period; and</p></li><li><p>Any other provisions to the mutual agreement of the landowner and the local governing body that further the purposes of this chapter.</p></li></ol><p>1982, c. 374, § 15.1-1513.7; 1997, c. 587.</p>',4),n=[r];function s(c,l,d,h,f,p){return t(),i("div",null,n)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
