import{_ as o,o as e,c as i,z as t,a as s}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-4914 Dissolution of authority; disposition of property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-4914.md","filePath":"vacode/15.2-4914.md"}'),a={name:"vacode/15.2-4914.md"},r=t("h1",{id:"§-15-2-4914-dissolution-of-authority-disposition-of-property",tabindex:"-1"},[s("§ 15.2-4914 Dissolution of authority; disposition of property. "),t("a",{class:"header-anchor",href:"#§-15-2-4914-dissolution-of-authority-disposition-of-property","aria-label":'Permalink to "§ 15.2-4914 Dissolution of authority; disposition of property."'},"​")],-1),h=t("p",null,"Whenever the board of directors of the authority by resolution determines that the purposes for which the authority was formed have been substantially complied with and all bonds theretofore issued and all obligations theretofore incurred by the authority have been fully paid, the then members of the board of directors of the authority shall thereupon execute and file for record with the governing body of the locality which created the authority, a resolution declaring such facts. If the governing body of the locality which created the authority is of the opinion that the facts stated in the authority's resolution are true and that the authority should be dissolved, it shall so resolve and the authority shall stand dissolved. Upon such dissolution, the title to all funds and properties owned by the authority at the time of such dissolution shall vest in the locality creating the authority and possession of such funds and properties shall forthwith be delivered to such locality.",-1),n=t("p",null,"1966, c. 651, § 15.1-1385; 1997, c. 587.",-1),d=[r,h,n];function l(c,u,f,p,y,_){return e(),i("div",null,d)}const v=o(a,[["render",l]]);export{m as __pageData,v as default};
