import{_ as t,o as e,c as o,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-5147 Powers of localities, etc., to make grants and conveyances to and contracts with authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5147.md","filePath":"vacode/15.2-5147.md"}'),r={name:"vacode/15.2-5147.md"},i=a('<h1 id="§-15-2-5147-powers-of-localities-etc-to-make-grants-and-conveyances-to-and-contracts-with-authority" tabindex="-1">§ 15.2-5147 Powers of localities, etc., to make grants and conveyances to and contracts with authority. <a class="header-anchor" href="#§-15-2-5147-powers-of-localities-etc-to-make-grants-and-conveyances-to-and-contracts-with-authority" aria-label="Permalink to &quot;§ 15.2-5147 Powers of localities, etc., to make grants and conveyances to and contracts with authority.&quot;">​</a></h1><p>Each political subdivision may:</p><ol><li><p>Convey or lease to any authority, with or without consideration, any system or portion thereof, or any right or interest in such facilities or any property appertaining thereto, upon such terms and conditions as the governing body determines to be in the best interest of such political subdivision;</p></li><li><p>Contract, jointly or severally, with any authority for the collection, treatment or disposal of sewage, industrial waste or refuse; and grant to such authority the right to receive, use and dispose of all or any portion of the refuse generated or collected by or within the jurisdiction or under the control of such unit; and in implementation of such contract or grant, exercise the powers set forth in §§ <a href="http://law.lis.virginia.gov/vacode/15.2-927/" target="_blank" rel="noreferrer">15.2-927</a> and <a href="http://law.lis.virginia.gov/vacode/15.2-928/" target="_blank" rel="noreferrer">15.2-928</a>; and</p></li><li><p>Contract with any authority for shutting off the supply of water furnished by any water system owned or operated by such political subdivision or under its jurisdiction or control to any premises connected with any sewer system of the authority if the owner, tenant or occupant of such premises fails to pay any rates, fees or charges for the use of or for the services furnished by such sewer system within the time or times specified in such contract.</p></li></ol><p>Code 1950, § 15-764.31; 1950, p. 1330; 1962, c. 623, § 15.1-1269; 1979, c. 280; 1997, c. 587; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0473" target="_blank" rel="noreferrer">473</a>.</p>',4),n=[i];function s(c,h,l,d,p,f){return e(),o("div",null,n)}const _=t(r,[["render",s]]);export{y as __pageData,_ as default};
