import{_ as o,o as t,c as s,z as e,a as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-522 Same; powers of county treasurer; deposit of moneys.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-522.md","filePath":"vacode/15.2-522.md"}'),n={name:"vacode/15.2-522.md"},a=e("h1",{id:"§-15-2-522-same-powers-of-county-treasurer-deposit-of-moneys",tabindex:"-1"},[r("§ 15.2-522 Same; powers of county treasurer; deposit of moneys. "),e("a",{class:"header-anchor",href:"#§-15-2-522-same-powers-of-county-treasurer-deposit-of-moneys","aria-label":'Permalink to "§ 15.2-522 Same; powers of county treasurer; deposit of moneys."'},"​")],-1),c=e("p",null,"The director of finance shall also exercise the powers conferred and perform the duties imposed by general law upon county treasurers, and shall be subject to the obligations and penalties imposed by general law. All moneys received by any county officer or employee for or in connection with county business shall be paid promptly into the hands of the director of finance. All such money shall be promptly deposited by the director of finance to the credit of the county in such banks or trust companies as the board selects. No money shall be disbursed or paid out by the county except upon checks signed by the chairman of the board, or such other person the board designates, and countersigned by the director of the department of finance.",-1),i=e("p",null,"The board may designate one or more banks or trust companies as a receiving or collecting agency under the direction of the department of finance. All funds so collected or received shall be deposited to the credit of the county in such banks or trust companies as the board selects.",-1),d=e("p",null,"Every bank or trust company serving as a depository or as a receiving or collecting agency for county funds shall be required by the board to give adequate security therefor and to meet such interest requirements as the board may by ordinance or resolution establish. All interest on money so deposited shall accrue to the county's benefit.",-1),l=e("p",null,"Code 1950, § 15-288; 1959, Ex. Sess., c. 69; 1962, cc. 399, 623, § 15.1-605; 1982, c. 647; 1984, c. 485; 1997, c. 587.",-1),h=[a,c,i,d,l];function u(p,f,y,m,b,_){return t(),s("div",null,h)}const w=o(n,[["render",u]]);export{v as __pageData,w as default};
