import{_ as o,o as i,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-5371 Decisions of Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5371.md","filePath":"vacode/15.2-5371.md"}'),r={name:"vacode/15.2-5371.md"},s=e("h1",{id:"§-15-2-5371-decisions-of-authority",tabindex:"-1"},[t("§ 15.2-5371 Decisions of Authority. "),e("a",{class:"header-anchor",href:"#§-15-2-5371-decisions-of-authority","aria-label":'Permalink to "§ 15.2-5371 Decisions of Authority."'},"​")],-1),n=e("p",null,"A majority of the Authority shall constitute a quorum for the purposes of conducting its business and exercising its powers and for all other purposes. Actions of the Authority shall receive the affirmative vote of a majority of the quorum to be effective. No vacancy on the Authority shall impair the right of a quorum to exercise all rights and perform all the duties of the Authority. The Authority shall determine the times and places of its regular meetings. Special meetings of the Authority shall be held when requested by two or more members of the Authority. Any such request for a special meeting shall be in writing, and the request shall specify the time and place of the meeting and the matters to be considered at the meeting. A reasonable effort shall be made to provide each member with notice of any special meeting. No matter not specified in the notice shall be considered at such special meeting unless all the members of the Authority are present.",-1),h=e("p",null,[t("2007, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0676",target:"_blank",rel:"noreferrer"},"676"),t("; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0575",target:"_blank",rel:"noreferrer"},"575"),t(".")],-1),l=[s,n,h];function c(f,d,u,m,p,g){return i(),a("div",null,l)}const b=o(r,[["render",c]]);export{y as __pageData,b as default};
