import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-5376 Issuance of bonds by participating localities and validation thereof.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5376.md","filePath":"vacode/15.2-5376.md"}'),n={name:"vacode/15.2-5376.md"},o=i('<h1 id="§-15-2-5376-issuance-of-bonds-by-participating-localities-and-validation-thereof" tabindex="-1">§ 15.2-5376 Issuance of bonds by participating localities and validation thereof. <a class="header-anchor" href="#§-15-2-5376-issuance-of-bonds-by-participating-localities-and-validation-thereof" aria-label="Permalink to &quot;§ 15.2-5376 Issuance of bonds by participating localities and validation thereof.&quot;">​</a></h1><p>Any participating locality may issue its general obligation bonds in the manner provided in the Public Finance Act (§ <a href="http://law.lis.virginia.gov/vacode/15.2-2600/" target="_blank" rel="noreferrer">15.2-2600</a> et seq.) in furtherance of the establishment, construction, or enlargement of a hospital or health center. The industrial development authority of any participating locality may issue its bonds in the manner provided in the Industrial Development and Revenue Bond Act (§ <a href="http://law.lis.virginia.gov/vacode/15.2-4900/" target="_blank" rel="noreferrer">15.2-4900</a> et seq.) in furtherance of the establishment, construction, enlargement, or operation of a nonprofit or not-for-profit hospital or health center with the concurrence of the governing body of the participating locality.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>.</p>',3),r=[o];function l(s,c,d,h,p,f){return t(),a("div",null,r)}const u=e(n,[["render",l]]);export{g as __pageData,u as default};
