import{_ as t,o as s,c as o,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-5417 Moneys received deemed trust funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5417.md","filePath":"vacode/15.2-5417.md"}'),r={name:"vacode/15.2-5417.md"},n=e("h1",{id:"§-15-2-5417-moneys-received-deemed-trust-funds",tabindex:"-1"},[a("§ 15.2-5417 Moneys received deemed trust funds. "),e("a",{class:"header-anchor",href:"#§-15-2-5417-moneys-received-deemed-trust-funds","aria-label":'Permalink to "§ 15.2-5417 Moneys received deemed trust funds."'},"​")],-1),d=e("p",null,"Notwithstanding any other provisions of law to the contrary, all moneys received pursuant to the authority of this chapter, whether as proceeds from the sale of bonds or as revenues, shall be deemed to be trust funds to be held and applied solely as provided in this chapter. The resolution authorizing the issuance of bonds or the trust agreement securing such bonds may provide that any of such moneys may be temporarily invested and reinvested, pending the disbursement thereof, in such securities and other investments as shall be provided in such resolution or trust agreement, and shall provide that any officer with whom, or any bank or trust company with which, such moneys are deposited shall act as trustee of such moneys and shall hold and apply the same for the purposes hereof, subject to such regulations as this chapter and the resolution or trust agreement may provide.",-1),i=e("p",null,"1979, c. 416, § 15.1-1620; 1997, c. 587.",-1),h=[n,d,i];function c(u,l,m,p,f,y){return s(),o("div",null,h)}const b=t(r,[["render",c]]);export{_ as __pageData,b as default};
