import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-5423.1 Exemption from taxation for certain authorities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5423.1.md","filePath":"vacode/15.2-5423.1.md"}'),o={name:"vacode/15.2-5423.1.md"},i=r('<h1 id="§-15-2-5423-1-exemption-from-taxation-for-certain-authorities" tabindex="-1">§ 15.2-5423.1 Exemption from taxation for certain authorities. <a class="header-anchor" href="#§-15-2-5423-1-exemption-from-taxation-for-certain-authorities" aria-label="Permalink to &quot;§ 15.2-5423.1 Exemption from taxation for certain authorities.&quot;">​</a></h1><p>An authority created by a governmental unit exempt from the referendum requirement of § <a href="http://law.lis.virginia.gov/vacode/15.2-5403/" target="_blank" rel="noreferrer">15.2-5403</a> is hereby declared to be performing a public function on behalf of the governmental unit that is the sole member of such authority with respect to which the authority is created and to be a public instrumentality of such governmental unit. Accordingly, an authority created by a governmental unit exempt from the referendum requirement of § <a href="http://law.lis.virginia.gov/vacode/15.2-5403/" target="_blank" rel="noreferrer">15.2-5403</a> shall be exempt from state and local taxation to the same extent that the governmental unit that is the sole member of such authority is exempt from such taxation.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0929" target="_blank" rel="noreferrer">929</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>.</p>',3),n=[i];function c(h,l,s,f,m,u){return t(),a("div",null,n)}const d=e(o,[["render",c]]);export{_ as __pageData,d as default};
