import{_ as o,o as r,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 15.2-5431.22 Trust agreement; bond resolution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5431.22.md","filePath":"vacode/15.2-5431.22.md"}'),a={name:"vacode/15.2-5431.22.md"},n=e("h1",{id:"§-15-2-5431-22-trust-agreement-bond-resolution",tabindex:"-1"},[t("§ 15.2-5431.22 Trust agreement; bond resolution. "),e("a",{class:"header-anchor",href:"#§-15-2-5431-22-trust-agreement-bond-resolution","aria-label":'Permalink to "§ 15.2-5431.22 Trust agreement; bond resolution."'},"​")],-1),i=e("p",null,"In the discretion of the authority, any revenue bonds issued under the provisions of this chapter may be secured by a trust agreement by and between the authority and a corporate trustee, which may be any trust company or bank having the powers of a trust company within or outside the Commonwealth. The resolution authorizing the issuance of the bonds or the trust agreement may pledge or assign the revenues to be received. The resolution or trust agreement may set forth the rights and remedies of the bondholders, and may restrict the individual right of action by bondholders as is customary in trust agreements or trust indentures securing bonds or debentures of corporations. The resolution or trust agreement may also contain such other provisions as the authority deems reasonable and proper for the security of the bondholders. Except as otherwise provided in this chapter, the authority may provide for the payment of the proceeds of the sale of the bonds and its revenues to such officer, board or depositary as it may designate for the custody thereof, and for the method of disbursement thereof, with such safeguards and restrictions as it may determine. All expenses incurred in carrying out the provisions of the resolution or trust agreement may be treated as part of the cost of operation.",-1),d=e("p",null,[t("2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0643",target:"_blank",rel:"noreferrer"},"643"),t(".")],-1),h=[n,i,d];function u(c,l,m,f,p,b){return r(),s("div",null,h)}const _=o(a,[["render",u]]);export{y as __pageData,_ as default};
