import{_ as o,o as n,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-5431.30 Refunding bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5431.30.md","filePath":"vacode/15.2-5431.30.md"}'),s={name:"vacode/15.2-5431.30.md"},a=e("h1",{id:"§-15-2-5431-30-refunding-bonds",tabindex:"-1"},[t("§ 15.2-5431.30 Refunding bonds. "),e("a",{class:"header-anchor",href:"#§-15-2-5431-30-refunding-bonds","aria-label":'Permalink to "§ 15.2-5431.30 Refunding bonds."'},"​")],-1),i=e("p",null,"An authority may provide by resolution for the issuance of revenue refunding bonds of the authority to refund any revenue bonds outstanding and issued under this chapter, whether or not such outstanding bonds have matured or are then subject to redemption. Proceeds of such revenue refunding bonds may be used to discharge the revenue bonds, or such revenue refunding bonds may be exchanged for the revenue bonds. Each such authority may provide by resolution for the issuance of a single issue of revenue bonds of the authority for the combined purposes of (i) paying the cost of any project or the improvement, extension, addition or reconstruction thereof, and (ii) refunding outstanding revenue bonds of the authority which have been issued under the provisions of this chapter, whether or not such outstanding bonds have matured or are then subject to redemption. The issuance of such bonds, the maturities and other details thereof, the rights and remedies of the bondholders, and the rights, powers, privileges, duties and obligations of the authority with respect to such bonds, shall be governed by the foregoing provisions of this chapter to the extent that they are applicable.",-1),d=e("p",null,[t("2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0643",target:"_blank",rel:"noreferrer"},"643"),t(".")],-1),h=[a,i,d];function u(c,f,b,p,l,g){return n(),r("div",null,h)}const _=o(s,[["render",u]]);exp