import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-5504 Powers of Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5504.md","filePath":"vacode/15.2-5504.md"}'),a={name:"vacode/15.2-5504.md"},r=i('<h1 id="§-15-2-5504-powers-of-authority" tabindex="-1">§ 15.2-5504 Powers of Authority. <a class="header-anchor" href="#§-15-2-5504-powers-of-authority" aria-label="Permalink to &quot;§ 15.2-5504 Powers of Authority.&quot;">​</a></h1><p>The Authority shall have the following powers together with all powers incidental thereto or necessary for the performance of those hereinafter stated:</p><ol><li><p>To sue and be sued and to prosecute and defend, at law or in equity, in any court having jurisdiction of the subject matter and of the parties;</p></li><li><p>To adopt and use a corporate seal and to alter the same at pleasure;</p></li><li><p>To contract and be contracted with;</p></li><li><p>To employ and pay compensation to such employees and agents, including attorneys, as the board of directors deem necessary in carrying on the business of the Authority;</p></li><li><p>To exercise all powers expressly given the Authority by the governing bodies of the participating localities which established the Authority and to establish bylaws and make all rules and regulations, not inconsistent with the provisions of this chapter, deemed expedient for the management of the Authority&#39;s affairs;</p></li><li><p>To borrow money and to accept contributions, grants and other financial assistance from the United States of America and agencies or instrumentalities thereof, the Commonwealth, or any political subdivision, agency, or public instrumentality of the Commonwealth;</p></li><li><p>To formulate a tourism development agenda for each participating locality in the LENOWISCO and Cumberland Plateau Planning District Commissions;</p></li><li><p>To receive and expend moneys on behalf of tourism development;</p></li><li><p>To coordinate the participating localities&#39; individual tourism plans; and</p></li><li><p>To form corporations, limited partnerships or limited liability companies for the purposes of fostering or promoting tourism, job creation, economic development, or the sale of goods manufactured and produced in Virginia.</p></li></ol><p>1993, c. 663, § 15.1-1399.21; 1997, c. 587; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0806" target="_blank" rel="noreferrer">806</a>.</p>',4),n=[r];function s(l,p,c,d,h,m){return t(),o("div",null,n)}const _=e(a,[["render",s]]);export{f as __pageData,_ as default};
