import{_ as o,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 15.2-5703 Members of authority; appointment, terms, compensation, etc.; officers, quorum.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5703.md","filePath":"vacode/15.2-5703.md"}'),n={name:"vacode/15.2-5703.md"},i=e("h1",{id:"§-15-2-5703-members-of-authority-appointment-terms-compensation-etc-officers-quorum",tabindex:"-1"},[t("§ 15.2-5703 Members of authority; appointment, terms, compensation, etc.; officers, quorum. "),e("a",{class:"header-anchor",href:"#§-15-2-5703-members-of-authority-appointment-terms-compensation-etc-officers-quorum","aria-label":'Permalink to "§ 15.2-5703 Members of authority; appointment, terms, compensation, etc.; officers, quorum."'},"​")],-1),s=e("p",null,"Each authority created hereunder, whether created by single or multiple localities, shall be governed by a board of not less than six members, but always an even number, appointed by the governing body of the locality. The board members shall be appointed for staggered four-year terms. Members of the governing body may be appointed to the board but shall not comprise a majority thereon.",-1),h=e("p",null,"When an authority is created by participating localities, each shall appoint at least two members, one of whom may be a member of the governing body. One-half of the members first appointed by each governing body shall serve for two years and one-half shall serve for four years. After the first appointment, the term of office of all members shall be four years. When one or more additional localities join an existing authority, each of such participating localities shall have not less than two members on the authority's board. The first members shall be appointed immediately upon the admission of the locality into the authority in the same manner as were the first members of the authority.",-1),m=e("p",null,"The members of the board of the authority shall elect one of their number chairman and shall elect a secretary and a treasurer who need not be members of the board of the authority. The offices of secretary and treasurer may be combined. A majority of the members of the authority shall constitute a quorum and the vote of a majority of such quorum shall be necessary for any action taken by the authority. No vacancy in the membership of the board of the authority shall impair the right of a quorum to exercise all the rights and perform all the duties of the authority.",-1),c=e("p",null,"Localities which created or thereafter joined the authority, by ordinance or resolution or concurrent ordinances or resolutions, may provide for the payment of compensation to the members of the authority; provided no compensation shall be paid for meetings not attended and for the reimbursement to each member of the authority the amount of his actual expenses necessarily incurred in the performance of that member's duties.",-1),l=e("p",null,[t("Code 1950, § 15-714.4; 1950, p. 1246; 1952, c. 401; 1962, c. 623, § 15.1-1231; 1978, cc. 37, 717; 1984, cc. 142, 255; 1995, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0626",target:"_blank",rel:"noreferrer"},"626"),t("; 1997, c. 587.")],-1),f=[i,s,h,m,c,l];function d(u,p,b,y,_,g){return a(),r("div",null,f)}const w=o(n,[["render",d]]);export{x as __pageData,w as default};
