import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-5804 Executive Director appointment; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5804.md","filePath":"vacode/15.2-5804.md"}'),r={name:"vacode/15.2-5804.md"},o=a('<h1 id="§-15-2-5804-executive-director-appointment-duties" tabindex="-1">§ 15.2-5804 Executive Director appointment; duties. <a class="header-anchor" href="#§-15-2-5804-executive-director-appointment-duties" aria-label="Permalink to &quot;§ 15.2-5804 Executive Director appointment; duties.&quot;">​</a></h1><p>A. The Authority shall appoint an Executive Director, who is the chief administrative officer and secretary of the Authority and serves at the pleasure of the Authority. The Executive Director shall be paid from funds as may be appropriated or received by the Authority.</p><p>B. In addition to any other duties set forth in this chapter, the Executive Director shall:</p><ol><li><p>Direct and supervise the administrative affairs and activities of the Authority in accordance with its rules, regulations, and policies;</p></li><li><p>Attend all meetings and keep minutes of all proceedings;</p></li><li><p>Approve all accounts for salaries, per diem payments, and allowable expenses of the Authority and its employees and consultants and approve all expenses incidental to the operation of the Authority;</p></li><li><p>Report and make recommendations to the Authority on the merits and status of any proposed facility; and</p></li><li><p>Perform any other duty that the Authority requires for carrying out the provisions of this chapter.</p></li></ol><p>1992, c. 823, § 15.1-227.74; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0613" target="_blank" rel="noreferrer">613</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0629" target="_blank" rel="noreferrer">629</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0950" target="_blank" rel="noreferrer">950</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1038" target="_blank" rel="noreferrer">1038</a>; 1997, c. 587.</p>',5),n=[o];function s(p,l,c,h,d,u){return t(),i("div",null,n)}const v=e(r,[["render",s]]);export{_ as __pageData,v as default};
