import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-5933 Entitlement to tax revenues derived from the operation of facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5933.md","filePath":"vacode/15.2-5933.md"}'),r={name:"vacode/15.2-5933.md"},i=o('<h1 id="§-15-2-5933-entitlement-to-tax-revenues-derived-from-the-operation-of-facilities" tabindex="-1">§ 15.2-5933 Entitlement to tax revenues derived from the operation of facilities. <a class="header-anchor" href="#§-15-2-5933-entitlement-to-tax-revenues-derived-from-the-operation-of-facilities" aria-label="Permalink to &quot;§ 15.2-5933 Entitlement to tax revenues derived from the operation of facilities.&quot;">​</a></h1><p>A. 1. Upon execution of a binding development agreement for a sports or entertainment project, the City of Virginia Beach shall be entitled, subject to appropriation, to sales and use tax revenues defined in this chapter. The State Comptroller shall remit such sales and use tax revenues to the City of Virginia Beach on a quarterly basis, subject to such reasonable processing delays as may be required by the Department of Taxation. The State Comptroller shall make such remittances to the City of Virginia Beach, as provided herein, notwithstanding any provisions to the contrary in the Virginia Retail Sales and Use Tax Act (§ <a href="/vacode/58.1-600/">58.1-600</a> et seq.).</p><ol start="2"><li>Any entitlement of the City of Virginia Beach to receive sales and use tax revenues pursuant to the provisions of this chapter shall expire on July 1 following the twentieth anniversary of the completion of construction of the sports or entertainment project.</li></ol><p>B. The local governing body of the City of Virginia Beach may, by ordinance or resolution, fix and revise from time to time and charge and collect rates, rents, fees, ticket surcharges, or other charges for a sports or entertainment project and any temporary sports or entertainment project developed under the provisions of this chapter.</p><p>C. If a sports and entertainment project qualifies for entitlement to sales and use tax revenues pursuant to the provisions of § <a href="/vacode/58.1-3851.1/">58.1-3851.1</a> or <a href="/vacode/58.1-3851.2/">58.1-3851.2</a>, the City of Virginia Beach shall remain eligible to receive sales and use tax revenues pursuant to the provisions of this chapter; however, the amount received pursuant to this chapter shall be reduced by the amount received pursuant to the provisions of § <a href="/vacode/58.1-3851.1/">58.1-3851.1</a> or <a href="/vacode/58.1-3851.2/">58.1-3851.2</a>.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0467" target="_blank" rel="noreferrer">467</a>.</p>',6),n=[i];function s(c,h,l,p,d,f){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{v as __pageData,m as default};
