import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-5948 Tax revenues of the Commonwealth or any other political subdivision not pledged.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-5948.md","filePath":"vacode/15.2-5948.md"}'),n={name:"vacode/15.2-5948.md"},i=a('<h1 id="§-15-2-5948-tax-revenues-of-the-commonwealth-or-any-other-political-subdivision-not-pledged" tabindex="-1">§ 15.2-5948 Tax revenues of the Commonwealth or any other political subdivision not pledged. <a class="header-anchor" href="#§-15-2-5948-tax-revenues-of-the-commonwealth-or-any-other-political-subdivision-not-pledged" aria-label="Permalink to &quot;§ 15.2-5948 Tax revenues of the Commonwealth or any other political subdivision not pledged.&quot;">​</a></h1><p>Nothing in this chapter shall be construed as authorizing the pledging of the faith and credit of the Commonwealth, or the faith and credit of any other political subdivision of the Commonwealth, for the payment of any bonds. No bonds issued pursuant to § <a href="/vacode/15.2-5943/">15.2-5943</a> shall pledge the full faith and credit of the Commonwealth, nor shall such bonds constitute a debt of the Commonwealth, and the bonds shall so state on their face. Bondholders shall have no recourse whatsoever against the Commonwealth for the payment of principal, interest, or redemption premium, if any, on such bonds.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0539" target="_blank" rel="noreferrer">539</a>.</p>',3),r=[i];function s(h,l,d,c,p,f){return o(),t("div",null,r)}const _=e(n,[["render",s]]);export{u as __pageData,_ as default};
