import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-6011 Eligible use of funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-6011.md","filePath":"vacode/15.2-6011.md"}'),r={name:"vacode/15.2-6011.md"},n=o('<h1 id="§-15-2-6011-eligible-use-of-funds" tabindex="-1">§ 15.2-6011 Eligible use of funds. <a class="header-anchor" href="#§-15-2-6011-eligible-use-of-funds" aria-label="Permalink to &quot;§ 15.2-6011 Eligible use of funds.&quot;">​</a></h1><p>The Authority is hereby empowered to pledge its funds, and make loans and grants to or for the benefit of for-profit enterprises or entities; governmental or corporate instrumentalities in the coalfield region of Virginia (including any political subdivision of the Commonwealth and the Breaks Interstate Park); not-for-profit enterprises or entities; nonprofit industrial development corporations; economic development authorities; or industrial development authorities for financing the following:</p><ol><li><p>Purchase of real estate;</p></li><li><p>Grading of site(s);</p></li><li><p>Construction of flood control dams;</p></li><li><p>Water, sewer, natural gas and electrical line replacement and extensions;</p></li><li><p>Construction or rehabilitation or expansion of buildings;</p></li><li><p>Construction of parking facilities;</p></li><li><p>Access roads construction and street improvements;</p></li><li><p>Purchase, lease, or relocation of machinery, tools, equipment, furniture, software, or other personal property;</p></li><li><p>Construction of improvements outside the Commonwealth if in the Breaks Interstate Park;</p></li><li><p>Feasibility studies, site studies, preliminary engineering or architectural reports, and other studies and plans;</p></li><li><p>Such other improvements, projects, activities, or purposes as the Authority deems necessary to accomplish its purpose; and</p></li><li><p>Costs and expenses associated with any item listed in subdivisions 1 through 11, including, but not limited to, architectural, engineering, consulting, legal, closing, installation, delivery, and assembly expenses.</p></li></ol><p>1988, c. 833, § 15.1-1646; 1990, c. 341; 1992, c. 32; 1993, c. 771; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>; 1997, c. 587; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0177" target="_blank" rel="noreferrer">177</a>.</p>',4),a=[n];function s(l,p,c,d,f,u){return i(),t("div",null,a)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
