import{_ as o,o as t,c as n,z as e,a as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-620 Same; powers of county treasurer; deposit of moneys.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-620.md","filePath":"vacode/15.2-620.md"}'),a={name:"vacode/15.2-620.md"},s=e("h1",{id:"§-15-2-620-same-powers-of-county-treasurer-deposit-of-moneys",tabindex:"-1"},[r("§ 15.2-620 Same; powers of county treasurer; deposit of moneys. "),e("a",{class:"header-anchor",href:"#§-15-2-620-same-powers-of-county-treasurer-deposit-of-moneys","aria-label":'Permalink to "§ 15.2-620 Same; powers of county treasurer; deposit of moneys."'},"​")],-1),c=e("p",null,"The director of finance shall exercise the powers conferred and perform the duties imposed by general law upon county treasurers, and shall be subject to the obligations and penalties imposed by general law. All moneys received by any county officer or employee for or in connection with county business shall be paid promptly into the hands of the director of finance. All such money shall be promptly deposited by the director of finance to the credit of the county in such banks or trust companies the board selects. No money shall be disbursed or paid out by the county except upon check signed by the chairman of the board, or such other person the board designates, and countersigned by the director of finance.",-1),i=e("p",null,"The director of finance or his authorized deputies may transfer public funds from one depository to another by wire. Such officers may also draw any of the county's money by check or by an electronic fund wire, or by any means deemed appropriate and sound by the director of finance and approved by the board, drawn upon a warrant issued by the board. If any money is knowingly paid otherwise than upon the director of finance's check or electronic fund wire or by alternative means specifically approved by the director of finance and the board, drawn upon such warrant, the payment shall be invalid against the county.",-1),d=e("p",null,"The board may designate one or more banks or trust companies as a receiving or collecting agency under the direction of the department of finance. All funds so collected or received shall be deposited to the credit of the county in such banks or trust companies as the board selects.",-1),h=e("p",null,"Every bank or trust company serving as a depository or as a receiving or collecting agency for county funds shall be required by the board to give adequate security therefor, and to meet such requirements as to interest thereon as the board may establish. All interest on money so deposited shall accrue to the benefit of the county.",-1),l=e("p",null,"Code 1950, § 15-320; 1954, c. 46; 1956, c. 349; 1959, Ex. Sess., c. 69; 1962, cc. 399, 623, § 15.1-640; 1982, c. 647; 1991, c. 16; 1997, c. 587.",-1),u=[s,c,i,d,h,l];function p(y,f,m,b,_,w){return t(),n("div",null,u)}const k=o(a,[["render",p]]);export{v as __pageData,k as default};
