import{_ as t,o as s,c as o,z as e,a as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-6317 Rents, fees and charges; disposition of revenues.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-6317.md","filePath":"vacode/15.2-6317.md"}'),a={name:"vacode/15.2-6317.md"},n=e("h1",{id:"§-15-2-6317-rents-fees-and-charges-disposition-of-revenues",tabindex:"-1"},[r("§ 15.2-6317 Rents, fees and charges; disposition of revenues. "),e("a",{class:"header-anchor",href:"#§-15-2-6317-rents-fees-and-charges-disposition-of-revenues","aria-label":'Permalink to "§ 15.2-6317 Rents, fees and charges; disposition of revenues."'},"​")],-1),i=e("p",null,"The rents, fees and charges established by the authority for the use of its property, projects and facilities and for any other service furnished or provided by the authority shall be fixed so that they, together with other revenues of the authority, shall provide at least sufficient funds to pay the cost of maintaining, repairing and operating the authority, its property, projects and facilities and the principal and interest of any bonds issued by the authority or other debts contracted as the same shall become due and payable. A reserve may be accumulated and maintained out of the revenues of the authority for extraordinary repairs and expenses and for such other purposes as may be provided in any resolution authorizing a bond issue or in any trust indenture securing such bonds. Subject to such provisions and restrictions as may be set forth in the resolution or in the trust indenture authorizing or securing any of the bonds or other obligations issued hereunder, the authority shall have exclusive control of the revenue derived from the operation of the authority and the right to use such revenues in the exercise of its powers and duties set forth in this chapter. No person, firm, association or corporation shall receive any profit or dividend from the revenues, earnings or other funds or assets of such authority other than for debts contracted, for services rendered, for materials and supplies furnished and for other value actually received by the authority.",-1),d=e("p",null,"Code 1950, § 15-955; 1954, c. 645; 1962, c. 623, § 15.1-1338; 1997, c. 587.",-1),h=[n,i,d];function c(u,f,p,l,y,v){return s(),o("div",null,h)}const _=t(a,[["render",c]]);export{b as __pageData,_ as default};
