import{_ as e,o,c as t,z as a,a as n}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 15.2-636 Examination and audit of books and accounts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-636.md","filePath":"vacode/15.2-636.md"}'),i={name:"vacode/15.2-636.md"},r=a("h1",{id:"§-15-2-636-examination-and-audit-of-books-and-accounts",tabindex:"-1"},[n("§ 15.2-636 Examination and audit of books and accounts. "),a("a",{class:"header-anchor",href:"#§-15-2-636-examination-and-audit-of-books-and-accounts","aria-label":'Permalink to "§ 15.2-636 Examination and audit of books and accounts."'},"​")],-1),s=a("p",null,"The board shall require an annual audit of the books of every county officer who handles public funds to be made by an accountant who is not a regular officer or employee of the county and who is qualified by training and experience. An audit made by the Auditor of Public Accounts under the provisions of law may be considered as having satisfied the requirements of this paragraph.",-1),d=a("p",null,"Either the board or the manager may at any time order an examination or audit of the accounts of any officer or department of the county government. Upon the death, resignation, removal or expiration of the term of any county officer, the director of finance shall cause an audit and investigation of the accounts of such officer to be made and shall report the results to the manager and the board. In case of the death, resignation or removal of the director of finance, the board shall cause an audit to be made of his accounts. If as a result of any such audit, an officer is found indebted to the county, the board shall proceed forthwith to collect such indebtedness.",-1),c=a("p",null,"Code 1950, § 15-330; 1962, c. 623, § 15.1-653; 1997, c. 587.",-1),h=[r,s,d,c];function f(u,l,m,p,b,_){return o(),t("div",null,h)}const v=e(i,[["render",f]]);export{x as __pageData,v as default};
