import{_ as a,o,c as t,z as e,a as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-702 County board; membership, terms, chairman, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-702.md","filePath":"vacode/15.2-702.md"}'),s={name:"vacode/15.2-702.md"},n=e("h1",{id:"§-15-2-702-county-board-membership-terms-chairman-etc",tabindex:"-1"},[r("§ 15.2-702 County board; membership, terms, chairman, etc. "),e("a",{class:"header-anchor",href:"#§-15-2-702-county-board-membership-terms-chairman-etc","aria-label":'Permalink to "§ 15.2-702 County board; membership, terms, chairman, etc."'},"​")],-1),i=e("p",null,'Under the county manager plan all of the legislative powers of the county, however conferred or possessed by it, shall be vested in a board of five members to be known as the county board ("the board"). The members of the board shall be elected in the manner hereinafter provided for terms of four years. The board shall elect one of its members as chairman, who shall preside over its meetings. The chairman shall be elected by the board annually and any vacancy in the office shall be filled by the board for the unexpired term. The chairman has the same powers and duties as other members of the board with a vote but no veto and is the official head of the county. With the exception of those officers whose election is provided for by popular vote in Article VII, Section 4 of the Constitution of Virginia, board members shall be the only elective county officials. The board shall be a body corporate and as such has the right to sue and be sued in the same manner as is now provided by law for boards of supervisors.',-1),h=e("p",null,"Code 1950, § 15-350; 1962, c. 623, § 15.1-674; 1971, Ex. Sess., c. 1; 1997, c. 587.",-1),c=[n,i,h];function d(l,m,b,f,p,u){return o(),t("div",null,c)}const v=a(s,[["render",d]]);export{_ as __pageData,v as default};
