import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-705.1 Instant runoff voting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-705.1.md","filePath":"vacode/15.2-705.1.md"}'),a={name:"vacode/15.2-705.1.md"},i=o('<h1 id="§-15-2-705-1-instant-runoff-voting" tabindex="-1">§ 15.2-705.1 Instant runoff voting. <a class="header-anchor" href="#§-15-2-705-1-instant-runoff-voting" aria-label="Permalink to &quot;§ 15.2-705.1 Instant runoff voting.&quot;">​</a></h1><p>A. For purposes of this section:</p><p>&quot;Instant runoff voting&quot; means a method of casting and tabulating votes in which (i) voters rank candidates in order of preference, (ii) tabulation proceeds in rounds such that in each round either a candidate or candidates are elected or the last-place candidate is defeated, (iii) votes for voters&#39; next-ranked candidates are transferred from elected or defeated candidates, and (iv) tabulation ends when the number of candidates elected equals the number of offices to be filled. &quot;Instant runoff voting&quot; is also known as &quot;ranked choice voting.&quot;</p><p>&quot;Ranking&quot; means the ordinal number assigned on a ballot by a voter to a candidate to express the voter&#39;s preference for that candidate. Ranking number one is the highest ranking, ranking number two is the next-highest ranking, and so on, consecutively, up to the number of candidates indicated on the ballot.</p><p>B. Elections to nominate candidates for and to elect members to the board of supervisors in a county operating under the county manager plan may be conducted by instant runoff voting pursuant to this section.</p><p>C. The State Board may promulgate regulations for the proper and efficient administration of elections determined by instant runoff voting, including (i) procedures for tabulating votes in rounds, (ii) procedures for determining winners in elections for offices to which only one candidate is being elected and for offices to which more than one candidate is being elected, and (iii) standards for ballots pursuant to § <a href="/vacode/24.2-613/">24.2-613</a>, notwithstanding the provisions of subsection E of that section.</p><p>D. The State Board may administer or prescribe standards for a voter outreach and public information program for use by any locality conducting instant runoff voting pursuant to this section.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0713" target="_blank" rel="noreferrer">713</a>.</p>',8),r=[i];function s(d,c,f,u,h,l){return t(),n("div",null,r)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
