import{_ as o,o as s,c as t,z as e,a as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-721 Civil service commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-721.md","filePath":"vacode/15.2-721.md"}'),a={name:"vacode/15.2-721.md"},r=e("h1",{id:"§-15-2-721-civil-service-commission",tabindex:"-1"},[i("§ 15.2-721 Civil service commission. "),e("a",{class:"header-anchor",href:"#§-15-2-721-civil-service-commission","aria-label":'Permalink to "§ 15.2-721 Civil service commission."'},"​")],-1),n=e("p",null,'The board, in addition to any other powers granted by general or special law may appoint a civil service commission ("the commission"), to be composed of five persons who shall receive such compensation as the board prescribes. The initial terms of office of commission members shall be staggered so that the terms of no more than two commissioners expire at one time. At the expiration of the term of each such member, his successor shall be appointed for a term of four years.',-1),c=e("p",null,"The commission, subject to the control of the board, shall establish and operate a classified civil service system for any or all classes of county employees, as designated by the board, which system shall provide for appointment, promotion, demotion, transfer, suspension, reinstatement, retirement and discharge of such employees. To this end it may establish a personnel administration and promulgate rules and regulations for the furtherance of the matters herein set out. The commission may appoint such employees and staff as it deems necessary subject to prior authorization of the board.",-1),m=e("p",null,"Notwithstanding any other provision of law, the commission may establish its own rules, regulations, or procedures to govern the conduct of hearings before the commission, including whether to permit rehearings.",-1),h=e("p",null,"Code 1950, § 15-355; 1962, c. 623, § 15.1-687; 1964, c. 64; 1978, c. 460; 1980, c. 79; 1989, c. 622; 1997, c. 587.",-1),l=[r,n,c,m,h];function d(p,f,u,_,v,b){return s(),t("div",null,l)}const w=o(a,[["render",d]]);export{g as __pageData,w as default};
