import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 15.2-7213 Deposit and investment of funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-7213.md","filePath":"vacode/15.2-7213.md"}'),o={name:"vacode/15.2-7213.md"},i=n('<h1 id="§-15-2-7213-deposit-and-investment-of-funds" tabindex="-1">§ 15.2-7213 Deposit and investment of funds. <a class="header-anchor" href="#§-15-2-7213-deposit-and-investment-of-funds" aria-label="Permalink to &quot;§ 15.2-7213 Deposit and investment of funds.&quot;">​</a></h1><p>All moneys of the Authority shall be deposited as soon as practicable in a separate account or accounts in one or more banks or trust companies organized under the laws of the Commonwealth or national banking associations having their principal offices in the Commonwealth. Such deposits shall be continuously secured in accordance with the Virginia Security for Public Deposits Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4400/" target="_blank" rel="noreferrer">2.2-4400</a> et seq.).</p><p>Funds of the Authority not needed for immediate use or disbursement may, subject to the provisions of any contract between the Authority and the holders of its bonds or any contract between the Authority and TVA, be invested in securities that are considered lawful investments for fiduciaries.</p><p>2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0210" target="_blank" rel="noreferrer">210</a>.</p>',4),r=[i];function s(c,d,l,h,f,p){return t(),a("div",null,r)}const m=e(o,[["render",s]]);export{_ as __pageData,m as default};
