import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 15.2-738 Modification of grievance procedure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-738.md","filePath":"vacode/15.2-738.md"}'),i={name:"vacode/15.2-738.md"},n=t('<h1 id="§-15-2-738-modification-of-grievance-procedure" tabindex="-1">§ 15.2-738 Modification of grievance procedure. <a class="header-anchor" href="#§-15-2-738-modification-of-grievance-procedure" aria-label="Permalink to &quot;§ 15.2-738 Modification of grievance procedure.&quot;">​</a></h1><p>Notwithstanding the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/2.2-1202.1/" target="_blank" rel="noreferrer">2.2-1202.1</a>, <a href="http://law.lis.virginia.gov/vacode/15.2-1506/" target="_blank" rel="noreferrer">15.2-1506</a>, and <a href="http://law.lis.virginia.gov/vacode/15.2-1507/" target="_blank" rel="noreferrer">15.2-1507</a> to the contrary, in any county which has the county manager plan of government provided for in this chapter, a grievance procedure may be established which permits an Equal Employment Opportunity officer, except the Director of the Department of Human Resource Management appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-1200/" target="_blank" rel="noreferrer">2.2-1200</a> and any employees thereof, to be present at any step of a grievance procedure established under § <a href="http://law.lis.virginia.gov/vacode/15.2-1506/" target="_blank" rel="noreferrer">15.2-1506</a>. Such officer shall not be an advocate or representative on behalf of either the grievant or management.</p><p>1989, c. 622, § 15.1-687.16; 1991, c. 143; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0770" target="_blank" rel="noreferrer">770</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>; 1997, c. 587; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1006" target="_blank" rel="noreferrer">1006</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),o=[n];function l(c,g,f,p,h,s){return r(),a("div",null,o)}const _=e(i,[["render",l]]);export{d as __pageData,_ as default};
