import{_ as s,o,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-740 Authority to impose assessments for local improvements; purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-740.md","filePath":"vacode/15.2-740.md"}'),r={name:"vacode/15.2-740.md"},a=e("h1",{id:"§-15-2-740-authority-to-impose-assessments-for-local-improvements-purposes",tabindex:"-1"},[t("§ 15.2-740 Authority to impose assessments for local improvements; purposes. "),e("a",{class:"header-anchor",href:"#§-15-2-740-authority-to-impose-assessments-for-local-improvements-purposes","aria-label":'Permalink to "§ 15.2-740 Authority to impose assessments for local improvements; purposes."'},"​")],-1),i=e("p",null,"The board may impose taxes or assessments upon owners of abutting property for making, improving, replacing, or enlarging the walkways upon then existing streets; for improving and paving then existing alleys; and for either the construction or the use of sanitary or storm water sewers including retaining walls, curbs, and gutters. However, such taxes or assessments shall not exceed the peculiar benefits resulting from the improvements to the owners of abutting property and no assessment for retaining walls shall be imposed upon any property owner who does not agree to such assessment.",-1),l=e("p",null,"In addition to the foregoing, the board may impose taxes or assessments upon owners of abutting property for the construction, replacement, or enlargement of sidewalks, waterlines, sanitary sewers, or storm water sewers; for the installation of street lights; for the construction or installation of canopies or other weather protective devices; for the installation of lighting in connection with the foregoing; and for permanent amenities, including, but not limited to, benches or waste receptacles, provided that such taxes or assessments shall not exceed the peculiar benefits resulting from the improvements to such owners of abutting property.",-1),p=e("p",null,[t("All assessments pursuant to this section shall be subject to the laws pertaining to assessments under Title 15.2, Chapter 24, Article 2 (§ "),e("a",{href:"http://law.lis.virginia.gov/vacode/15.2-2404/",target:"_blank",rel:"noreferrer"},"15.2-2404"),t(" et seq.), mutatis mutandis. All assessments pursuant to this section may also be made subject to installment payments and other provisions allowed for local assessments under this article.")],-1),c=e("p",null,'As used in this section, "owners of abutting property" includes the owners of property that abuts a state highway when the improvement is funded solely by county revenues.',-1),h=e("p",null,"1990, c. 323, § 15.1-687.18; 1997, c. 587.",-1),m=[a,i,l,p,c,h];function u(d,f,g,w,_,v){return o(),n("div",null,m)}const x=s(r,[["render",u]]);export{b as __pageData,x as default};
