import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 15.2-7502 Public hearing required prior to creation or designation of a land bank entity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-7502.md","filePath":"vacode/15.2-7502.md"}'),i={name:"vacode/15.2-7502.md"},r=t('<h1 id="§-15-2-7502-public-hearing-required-prior-to-creation-or-designation-of-a-land-bank-entity" tabindex="-1">§ 15.2-7502 Public hearing required prior to creation or designation of a land bank entity. <a class="header-anchor" href="#§-15-2-7502-public-hearing-required-prior-to-creation-or-designation-of-a-land-bank-entity" aria-label="Permalink to &quot;§ 15.2-7502 Public hearing required prior to creation or designation of a land bank entity.&quot;">​</a></h1><p>The governing body of a locality shall not adopt an ordinance creating a land bank entity pursuant to § <a href="/vacode/15.2-7501/">15.2-7501</a> or designating a planning district commission or an existing nonprofit entity pursuant to § <a href="/vacode/15.2-7512/">15.2-7512</a> until notice of intention to do so has been published once a week for two successive weeks in some newspaper published or having general circulation in the locality. The notice shall specify the time and place of a hearing at which affected or interested persons may appear and present their views, not less than five days nor more than 21 days after the second advertisement appears in such newspaper. After the public hearing has been conducted pursuant to this section, the governing body shall be empowered to create a land bank entity or designate a planning district commission or an existing nonprofit entity.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0159" target="_blank" rel="noreferrer">159</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0383" target="_blank" rel="noreferrer">383</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0147" target="_blank" rel="noreferrer">147</a>.</p>',3),o=[r];function s(c,d,l,p,h,g){return a(),n("div",null,o)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
