import{_ as e,o as a,c as s,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 15.2-846 Salaries and expenses of board members; administrative staff.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-846.md","filePath":"vacode/15.2-846.md"}'),i={name:"vacode/15.2-846.md"},r=t('<h1 id="§-15-2-846-salaries-and-expenses-of-board-members-administrative-staff" tabindex="-1">§ 15.2-846 Salaries and expenses of board members; administrative staff. <a class="header-anchor" href="#§-15-2-846-salaries-and-expenses-of-board-members-administrative-staff" aria-label="Permalink to &quot;§ 15.2-846 Salaries and expenses of board members; administrative staff.&quot;">​</a></h1><p>The board shall establish the salaries and allowances of board members in accordance with the provisions of general law provided:</p><ol><li><p>A public hearing shall be held on the salaries to be established;</p></li><li><p>No increase in such salaries shall be effective until the expiration of the current term of all board members whose salaries are to be increased; and</p></li><li><p>Any action or procedure necessary to be taken to increase such salaries shall be completed not later than April 15 of any year in which there is an election for board members.</p></li></ol><p>Each board member, in addition to salary and allowances, shall be entitled to reasonable administrative staff support paid by the county in conformity with existing pay scales and whose duty shall be limited exclusively to county business.</p><p>1975, c. 143, § 15.1-778.1; 1978, c. 382; 1982, c. 376; 1983, c. 152; 1988, c. 879; 1997, c. 587.</p>',5),o=[r];function n(l,d,c,h,p,m){return a(),s("div",null,o)}const _=e(i,[["render",n]]);export{f as __pageData,_ as default};
