import{_ as e,o,c as n,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-912 Regulation of tattoo parlors and body-piercing salons; definition; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-912.md","filePath":"vacode/15.2-912.md"}'),r={name:"vacode/15.2-912.md"},a=t('<h1 id="§-15-2-912-regulation-of-tattoo-parlors-and-body-piercing-salons-definition-exception" tabindex="-1">§ 15.2-912 Regulation of tattoo parlors and body-piercing salons; definition; exception. <a class="header-anchor" href="#§-15-2-912-regulation-of-tattoo-parlors-and-body-piercing-salons-definition-exception" aria-label="Permalink to &quot;§ 15.2-912 Regulation of tattoo parlors and body-piercing salons; definition; exception.&quot;">​</a></h1><p>A. Any locality may by ordinance regulate the sanitary condition of the personnel, equipment and premises of tattoo parlors and body-piercing salons and specify procedures for enforcement of compliance with the disease control and disclosure requirements of § <a href="http://law.lis.virginia.gov/vacode/18.2-371.3/" target="_blank" rel="noreferrer">18.2-371.3</a>.</p><p>B. For the purposes of this section:</p><p>&quot;Body-piercing salon&quot; means any place in which a fee is charged for the act of penetrating the skin to make a hole, mark, or scar, generally permanent in nature. &quot;Body piercing&quot; does not include the use of a mechanized, presterilized ear-piercing system that penetrates the outer perimeter or lobe of the ear or both.</p><p>&quot;Tattoo parlor&quot; means any place in which is offered or practiced the placing of designs, letters, scrolls, figures, symbols or any other marks upon or under the skin of any person with ink or any other substance, resulting in the permanent coloration of the skin, including permanent make-up or permanent jewelry, by the aid of needles or any other instrument designed to touch or puncture the skin.</p><p>C. This section shall not apply to medical doctors, veterinarians, registered nurses or any other medical services personnel licensed pursuant to Title 54.1 in performance of their professional duties.</p><p>D. Localities requiring regulation of tattoo parlors and piercing salons by ordinance shall include in such ordinance authorization for unannounced inspections by appropriate personnel of the locality.</p><p>1983, c. 429, § 15.1-28.3; 1997, c. 587; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0270" target="_blank" rel="noreferrer">270</a>.</p>',8),i=[a];function s(c,l,p,d,h,f){return o(),n("div",null,i)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
