import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 15.2-915.5 Disposition of firearms acquired by localities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-915.5.md","filePath":"vacode/15.2-915.5.md"}'),r={name:"vacode/15.2-915.5.md"},o=t('<h1 id="§-15-2-915-5-disposition-of-firearms-acquired-by-localities" tabindex="-1">§ 15.2-915.5 Disposition of firearms acquired by localities. <a class="header-anchor" href="#§-15-2-915-5-disposition-of-firearms-acquired-by-localities" aria-label="Permalink to &quot;§ 15.2-915.5 Disposition of firearms acquired by localities.&quot;">​</a></h1><p>A. No locality or agent of such locality may participate in any program in which individuals are given a thing of value provided by another individual or other entity in exchange for surrendering a firearm to the locality or agent of such locality unless the governing body of the locality has enacted an ordinance, pursuant to § <a href="/vacode/15.2-1425/">15.2-1425</a>, authorizing the participation of the locality or agent of such locality in such program.</p><p>B. Any ordinance enacted pursuant to this section shall require that any firearm received, except a firearm of the type defined in § <a href="/vacode/18.2-288/">18.2-288</a> or <a href="/vacode/18.2-299/">18.2-299</a> or a firearm the transfer for which is prohibited by federal law, shall be destroyed by the locality unless the person surrendering the firearm requests in writing that the firearm be offered for sale by public auction or sealed bids to a person licensed as a dealer pursuant to 18 U.S.C. § 921 et seq. Notice of the date, time, and place of any sale conducted pursuant to this subsection shall be given by advertisement in at least two newspapers published and having general circulation in the Commonwealth, at least one of which shall have general circulation in the locality in which the property to be sold is located. At least 30 days shall elapse between publication of the notice and the auction or the date on which sealed bids will be opened. Any firearm remaining in possession of the locality or agent of the locality after attempts to sell at public auction or by sealed bids shall be disposed of in a manner the locality deems proper, which may include destruction of the firearm or, subject to any registration requirements of federal law, sale of the firearm to a licensed dealer.</p><p>2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0211" target="_blank" rel="noreferrer">211</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1205" target="_blank" rel="noreferrer">1205</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1247" target="_blank" rel="noreferrer">1247</a>.</p>',4),n=[o];function s(l,c,h,d,f,p){return a(),i("div",null,n)}const g=e(r,[["render",s]]);export{b as __pageData,g as default};
