import{_ as o,o as n,c as t,z as e,a}from"./chunks/framework.9482e208.js";const q=JSON.parse('{"title":"§ 15.2-921 Ordinances requiring fencing of swimming pools.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-921.md","filePath":"vacode/15.2-921.md"}'),i={name:"vacode/15.2-921.md"},s=e("h1",{id:"§-15-2-921-ordinances-requiring-fencing-of-swimming-pools",tabindex:"-1"},[a("§ 15.2-921 Ordinances requiring fencing of swimming pools. "),e("a",{class:"header-anchor",href:"#§-15-2-921-ordinances-requiring-fencing-of-swimming-pools","aria-label":'Permalink to "§ 15.2-921 Ordinances requiring fencing of swimming pools."'},"​")],-1),r=e("p",null,"For the purposes of this section:",-1),c=e("p",null,'"Fence" means a close type vertical barrier not less than four feet in height above ground surface. A woven steel wire, chain link, picket or solid board type fence or a fence of similar construction which will prevent the smallest of children from getting through shall be construed as within this definition.',-1),l=e("p",null,'"Swimming pool" includes any outdoor man-made structure constructed from material other than natural earth or soil designed or used to hold water for the purpose of providing a swimming or bathing place for any person or any such structure for the purpose of impounding water therein to a depth of more than two feet.',-1),h=e("p",null,"Any locality may adopt ordinances making it unlawful for any person to construct, maintain, use, possess or control any pool on any property in such locality, without having a fence completely around such swimming pool. Such ordinances also may provide that every gate in such fence shall be capable of being securely fastened at a height of not less than four feet above ground level; that it shall be unlawful for any such gate to be allowed to remain unfastened while the pool is not in use; and that such fence shall be constructed so as to come within two inches of the ground at the bottom and shall be at least five feet from the edge of the pool at any point.",-1),d=e("p",null,"Violation of any such ordinance may be made punishable by a fine of not more than $300 or confinement in jail for not more than thirty days, either or both. Each day's violation may be construed as a separate offense.",-1),f=e("p",null,"Any such ordinance may be made applicable to swimming pools constructed before, as well as those constructed after, the adoption thereof. No such ordinance shall take effect less than ninety days from the adoption thereof, nor shall any such ordinance apply to any swimming pool operated by or in conjunction with any hotel located on a government reservation.",-1),u=e("p",null,"Code 1950, § 15-18.1; 1958, c. 123; 1962, c. 623, § 15.1-29; 1997, c. 587.",-1),p=[s,r,c,l,h,d,f,u];function m(g,y,_,w,b,v){return n(),t("div",null,p)}const x=o(i,[["render",m]]);export{q as __pageData,x as default};
