import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-926.3 Local regulation of certain aircraft.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-926.3.md","filePath":"vacode/15.2-926.3.md"}'),i={name:"vacode/15.2-926.3.md"},r=t('<h1 id="§-15-2-926-3-local-regulation-of-certain-aircraft" tabindex="-1">§ 15.2-926.3 Local regulation of certain aircraft. <a class="header-anchor" href="#§-15-2-926-3-local-regulation-of-certain-aircraft" aria-label="Permalink to &quot;§ 15.2-926.3 Local regulation of certain aircraft.&quot;">​</a></h1><p>A. No political subdivision may regulate the use of a privately owned, unmanned aircraft system as defined in § <a href="/vacode/19.2-60.1/">19.2-60.1</a> within its boundaries.</p><p>B. Notwithstanding the prohibition of subsection A, a political subdivision may, by ordinance or regulation, regulate the take-off and landing of an unmanned aircraft, as defined in § <a href="/vacode/19.2-60.1/">19.2-60.1</a>, on property owned by the political subdivision. Such ordinance or regulation shall be developed and authorized in accordance with the rules and regulations promulgated by the Department of Aviation (the Department). Such rules and regulations shall be in accordance with federal rules and regulations and shall include a process for adoption of an ordinance or regulation, exemptions to the ordinance or regulation, political subdivision training, and notification requirements. The political subdivision shall report to the Department any ordinance or regulation adopted pursuant to this section, and the Department shall publish and update annually on its website, and any other website the Department deems appropriate, a summary of any such ordinance or regulation adopted.</p><p>C. Nothing in this section shall permit a person to go or enter upon land owned by a political subdivision solely because he is in possession of an unmanned aircraft system if he would not otherwise be permitted entry upon such land.</p><p>D. Nothing in this section shall be construed to prohibit (i) the take-off or landing of an unmanned aircraft by a commercial operator in compliance with Federal Aviation Administration regulations, or as deemed reasonable or necessary by private or public entities for emergency or maintenance support functions or services, including the protection and maintenance of public or private critical infrastructure; (ii) the landing of an unmanned aircraft by an operator in compliance with Federal Aviation Administration regulations as deemed reasonable or necessary by the operator in the event of a technical malfunction of an unmanned aircraft system; (iii) the take-off or landing of an unmanned aircraft being operated by a sworn public safety officer in the performance of his duties; or (iv) the take-off or landing of an unmanned aircraft owned or operated by the United States government, or any operator under contract with any agency of the United States government, in performance of his assigned duties.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0451" target="_blank" rel="noreferrer">451</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',6),o=[r];function s(l,c,d,p,f,u){return a(),n("div",null,o)}const m=e(i,[["render",s]]);export{g as __pageData,m as default};
