import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 15.2-965.1 Participation of small, women-owned, and minority-owned businesses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-965.1.md","filePath":"vacode/15.2-965.1.md"}'),t={name:"vacode/15.2-965.1.md"},r=n('<h1 id="§-15-2-965-1-participation-of-small-women-owned-and-minority-owned-businesses" tabindex="-1">§ 15.2-965.1 Participation of small, women-owned, and minority-owned businesses. <a class="header-anchor" href="#§-15-2-965-1-participation-of-small-women-owned-and-minority-owned-businesses" aria-label="Permalink to &quot;§ 15.2-965.1 Participation of small, women-owned, and minority-owned businesses.&quot;">​</a></h1><p>A. Any locality may enact an ordinance providing that whenever there exists (i) a rational basis for small business enhancement, or (ii) a persuasive analysis that documents a statistically significant disparity between the availability and utilization of women-owned and minority-owned businesses, the chief executive of the local governing entity shall be authorized and encouraged to require implementation of appropriate enhancement and remedial measures consistent with prevailing law.</p><p>B. A small, women-owned, or minority-owned business that is certified by the Department of Small Business and Supplier Diversity pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-1606/" target="_blank" rel="noreferrer">2.2-1606</a> shall not be required by any locality to obtain any additional certification to participate in any program designed to enhance the participation of such businesses as vendors or to remedy any documented disparity.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0865" target="_blank" rel="noreferrer">865</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>.</p>',4),o=[r];function s(l,c,d,p,f,h){return a(),i("div",null,o)}const u=e(t,[["render",s]]);export{g as __pageData,u as default};
